import React from "react";
import "./SearchForm.css";

import { useEffect, useContext } from 'react'
import useFormWithValidation from '../../../hooks/useForm'
import { useLocation } from 'react-router-dom'

import ErrorContext from '../../../contexts/ErrorContext'

function SearchForm({ isCheck, changeShort, searchedMovie, searchMovies, firstEntrance, savedMovie }) {

  const { pathname } = useLocation()
  const { values, handleChange, reset } = useFormWithValidation()

  useEffect(() => {
    if ((pathname === '/saved-movies' && savedMovie.length === 0)) {
      reset({ search: '' })
    } else {
      reset({ search: searchedMovie })
    }
  }, [searchedMovie, reset, pathname, savedMovie])

  function onSubmit(evt) {
    evt.preventDefault()
    if (evt.target.search.value) {
      searchMovies(evt.target.search.value)
    } else {
      setIsError(true)
    }
  }

  return (
    <section className="search">
      <form 
        className="search__form"
        name={'SearchForm'} 
        onSubmit={onSubmit}
        noValidate
      >
        <div className="search__box">
          <input 
            className="search__input" 
            placeholder="Фильм" 
            required

            type="text"
            name='search'
            value={values.search || ''}
            onChange={(evt) => {
              handleChange(evt)
              setIsError(false)
            }}

            disabled={savedMovie ? (savedMovie.length === 0 && true) : false}
            >
          </input>

          <button className={`search__button ${savedMovie ? (pathname === '/saved-movies' && savedMovie.length === 0) && 'search__submit_disabled' : ''}`} type="submit">
            Найти
          </button>

        </div>


        <div className="search__checkbox">
          <label className="search__tumbler">
            <input type="checkbox" />
            <span className="search__slider"></span>
          </label>
          <p className="search__tumbler-title">Короткометражки</p>
        </div>
      </form>
    </section>
  );
}

export default SearchForm;
