import React from "react";
import "./SearchForm.css";

import { useEffect, useContext } from "react";
import useFormWithValidation from "../../../hooks/useForm";
import { useLocation } from "react-router-dom";

import ErrorContext from "../../../contexts/ErrorContext";

function SearchForm({ getSearchMovies, onFilterMovies, isShortMovies }) {

  const [isQueryError, setIsQueryError] = useState(false)
  const location = useLocation()
  const [query, setQuery] = useState("")

  useEffect(() => {
    if (
      location.pathname === "/movies" &&
      localStorage.getItem("movieSearch")
    ) {
      const localQuery = localStorage.getItem("movieSearch")
      setQuery(localQuery)
    }
  }, [location])

  function editProfileInfo(e) {
    e.preventDefault()
    if (query.trim().length === 0) {
      setIsQueryError(true)
    } else {
      setIsQueryError(false)
      getSearchMovies(query)
    }
  }

  function inputChange(e) {
    setQuery(e.target.value)
  }
  return (
    <section className="search">
      <form
        className="search__form"
        name={"SearchForm"}
        onSubmit={editProfileInfo}
        noValidate
      >
        <div className="search__box">
          <input
            className="search__input"
            placeholder="Фильм"
            required
            type="text"
            name="search"
            value={values.search || ""}
            onChange={(evt) => {
              handleChange(evt);
            }}
            disabled={savedMovie ? savedMovie.length === 0 && true : false}
          ></input>

          <button
            className={`search__button ${
              savedMovie
                ? pathname === "/saved-movies" &&
                  savedMovie.length === 0 &&
                  "search__submit_disabled"
                : ""
            }`}
            type="submit"
          >
            Найти
          </button>
        </div>

        <div className="search__checkbox">
          <label className="search__tumbler">
            <input type="checkbox" />
            <span className="search__slider"></span>
          </label>
          <p className="search__tumbler-title">Короткометражки</p>
        </div>
      </form>
    </section>
  );
}

export default SearchForm;
