import React, { useEffect, useState } from "react";
import { useLocation } from "react-router-dom";

import "./MoviesCard.css";


function MoviesCard({ onLike, onDislike, card, savedMovies }) {
  const { pathname } = useLocation()
  const [click, setClick] = useState(false)

  function onCardClick() {
    if (saved) {
      onDeleteCard(savedMovies.filter((m) => m.movieId === card.id)[0])
    } else {
      getLikeMovie(card)
    }
  }

  function onDislike() {
    onDislike(card)
  }

  function convertMovieDuration(duration) {
    const minutes = duration % 60;
    const hours = Math.floor(duration / 60);
    return `${hours}ч${minutes}м`;
  }

  return (
    <>
      <article className="movie">

        <a
          href={card.trailerLink}
          className="movie__link"
          target="_blank"
        >
          <img
            className="movie__poster"
            src={pathname === '/movies' ? `https://api.nomoreparties.co${card.image.url}` : card.image} 
            alt={card.name} 
          />
        </a>

        <div className="movie__info">
        <h2 className="movie__title">{card.nameRU}</h2>


        {pathname === '/movies' ? 
            <button
              className={`movie__button ${click ? 'movie__button-active' : ''}`}
              type="button"
              onClick={onClick}
            >
            </button>
           : 
            <button
              className="movie__button-dislike"
              type="button"
              onClick={() => onDislike(card._id)}
            ></button>
          }


        <p className="movie__length">{convertMovieDuration(card.duration)}</p>
          </div>
      </article>



    </>
  );
}

export default MoviesCard;
