class ApiMain {
  constructor(options) {
    this._url = options.url;
  }

  _checkResponse(res) {
    if (res.ok) {
      return Promise.resolve(res.json());
    }
    return Promise.reject(`Ошибка ${res.status}`);
  }

  //Регистрация и авторизация
  _request(url, options) {
    return fetch(`${this._url}${url}`, options)
    .then(this._checkResponse);
  }

  registration(username, email, password) {
    return this._request("/signup", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify({
        name: username,
        email: email,
        password: password,
      }),
    });
  }

  authorization(email, password) {
    return this._request("/signin", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify({
        email: email,
        password: password,
      }),
    });
  }

   //Регистрация и авторизация
   _request(path, method, data) {
    let body = data;
    if (method === "POST" && data) {
      body = JSON.stringify(data);
    }
    return fetch(this._url + path, {
      method,
      headers: {
        "Content-Type": "application/json",
      },
      body,
    }).then(this._checkResponse);
  }

  registration({ name, email, password }) {
    return this._request(`/signup`, "POST", { name, email, password });
  }

  authorization({ email, password }) {
    return this._request(`/signin`, "POST", { email, password });
  }




  //Получение данных
  getUserData(token) {
    return this._request("/users/me", {
      headers: {
        authorization: `Bearer ${token}`,
      },
    });
  }

  async getUserInfo() {
    const response = await fetch(`${this._url}/users/me`, {
      method: "GET",
      headers: {
        "Content-Type": "application/json",
        authorization: `Bearer ${localStorage.getItem("token")}`,
      },
    });
    return this._checkResponse(response);
  }

  //Редактирование данных пользователя
  async setUserInfo(data) {
    const response = await fetch(`${this._url}/users/me`, {
      method: "PATCH",
      headers: {
        "Content-Type": "application/json",
        authorization: `Bearer ${localStorage.getItem("token")}`,
      },
      body: JSON.stringify({
        name: data.name,
        about: data.about,
      }),
    });
    return this._checkResponse(response);
  }

  //

  getMovies(token) {
    return this._request("/movies", {
      headers: {
        authorization: `Bearer ${token}`,
      },
    });
  }

  addMovie(data, token) {
    return this._request("/movies", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
        Authorization: `Bearer ${token}`,
      },
      body: JSON.stringify({
        country: data.country,
        director: data.director,
        duration: data.duration,
        description: data.description,
        year: data.year,
        image: `https://api.nomoreparties.co${data.image.url}`,
        trailerLink: data.trailerLink,
        thumbnail: `https://api.nomoreparties.co${data.image.formats.thumbnail.url}`,
        movieId: data.id,
        nameRU: data.nameRU,
        nameEN: data.nameEN,
      }),
    });
  }

  deleteMovie(cardId, token) {
    return this._request(`/movies/${cardId}`, {
      method: "DELETE",
      headers: {
        Authorization: `Bearer ${token}`,
      },
    });
  }
}

export const apiMain = new ApiMain({
  url: "http://localhost:3000",
  //url: "https://movies.nomoredomainsrocks.ru",
});
