import React from "react"
import "./Notification.css"

function InfoToolTip(props, name) {
  return (
    <div
      className={`popup popup_type_tooltip ${
        props.isOpen ? "popup_opened" : ""
      }`}
      onClick={props.onCloseOverlay}
    >


      <div className="popup__container">
        if (props.isSuccess & name === "profile") return ? (
          <>
            <p className="popup__signup-title">Успешно!</p>
          </>
        ) : (

          props.isSuccess & name === "signin" ? (
            <>
              <p className="popup__signup-title">Молодец!</p>
            </>




        ) : (
          <>
            <p className="popup__signup-title">
              Что-то пошло не так. Попробуйте ещё раз!
            </p>
          </>
        )}

        <button
          type="button"
          className="popup__close-button"
          onClick={props.onClose}
        ></button>
      </div>
    </div>
  )
}

export default InfoToolTip
