import React, { useEffect, useState } from "react";

//Стили
import "./SavedMovies.css";

//Компоненты
import Header from "../Header/Header.js";
import SearchForm from "../Movies/SearchForm/SearchForm.js";
import MoviesCardList from "../Movies/MoviesCardList/MoviesCardList.js";
import Footer from "../Footer/Footer.jsx";

function SavedMovies({ isLoggedIn, savedMovies, onDislike }) {
  const [isShortMovies, setisShortMovies] = useState(false);
  const [searchQuery, setSearchQuery] = useState("");
  const [filteredMovies, setFilteredMovies] = useState(savedMovies);
  const [isNotFound, setIsNotFound] = useState(false);

  //из полезного

  function counterDurationMovie(movies) {
    return movies.filter((movie) => movie.duration < MAX_DURATION_MOVIE);
  }

  function filterMovies(movies, query) {
    const moviesQuery = movies.filter((movie) => {
      const movieRu = String(movie.nameRU).toLowerCase().trim();
      const movieEn = String(movie.nameEN).toLowerCase().trim();
      const userQuery = query.toLowerCase().trim();
      return (
        movieRu.indexOf(userQuery) !== -1 || movieEn.indexOf(userQuery) !== -1
      );
    });
    return moviesQuery;
  }

  useEffect(() => {
    if (filteredMovies.length === 0) {
      setIsNotFound(true);
    } else {
      setIsNotFound(false);
    }
  }, [filteredMovies]);

  useEffect(() => {
    const moviesFilmList = filterMovies(savedMovies, searchQuery);
    setFilteredMovies(
      isShortMovies ? counterDurationMovie(moviesFilmList) : moviesFilmList
    );
  }, [savedMovies, isShortMovies, searchQuery]);

  function searchMovies(query) {
    setSearchQuery(query);
  }

  function shortMoviesToggle() {
    setisShortMovies(!isShortMovies);
  }

  return (
    <>
      <Header isLoggedIn={isLoggedIn} />
      <main className="saved-movies">
        <SearchForm
          onFilterMovies={shortMoviesToggle}
          searchMovies={searchMovies}
        />
        <MoviesCardList
          cards={filteredMovies}
          isSavedFilms={true}
          savedMovies={savedMovies}
          onDislike={onDislike}
          isNotFound={isNotFound}
        />
      </main>
      <Footer />
    </>
  );
}

export default SavedMovies;
