import React, { useEffect, useState }  from "react";
import { useLocation } from 'react-router-dom';

import "./MoviesCardList.css";

import MoviesCard from "../MoviesCard/MoviesCard.js";
import Preloader from "../../Preloader/Preloader.js"

import { DESKTOP_COUNTER_MOVIES,
  TABLET_COUNTER_MOVIES,
  MOBILE_COUNTER_MOVIES, } from "../../../utils/constants.js";


function MoviesCardList({ onDislike, onLike, cards,
  isLoading,
  isSavedFilms,
  savedMovies,
  isReqError,
  isNotFound
}) {
  const { pathname } = useLocation();
  const [shownMovies, setShownMovies] = useState(0)

    function showMoviesDisplay() {
    const display = window.innerWidth
    if (display > 1023) {
      setShownMovies(12)
    } else if (display > 767) {
      setShownMovies(8)
    } else {
      setShownMovies(5)
    }
  }

  useEffect(() => {
    showMoviesDisplay()
  }, [cards])

  useEffect(() => {
    setTimeout(() => {
      window.addEventListener("resize", showMoviesDisplay)
    }, 500)
  })

  function getShowCountMovies() {
    const display = window.innerWidth
    if (display > 1023) {
      setShownMovies(shownMovies + DESKTOP_COUNTER_MOVIES)
    } else if (display > 767) {
      setShownMovies(shownMovies + TABLET_COUNTER_MOVIES)
    } else {
      setShownMovies(shownMovies + MOBILE_COUNTER_MOVIES)
    }
  }

  function getSavedMovieFromList(savedMovies, card) {
    return savedMovies.find((savedMovie) => savedMovie.movieId === card.id)
  }





  return (
    <section className="movies-card">
        <div className="movies-card__box">



              return (
                <MoviesCard
                  key={card.id}
                  savedMovies={isSavedFilms ? card._id : card.id}
                  onLike={onLike}
                  card={card}

                    cards={cards}
                    saved={getSavedMovieFromList(savedMovies, card)}

                    onDislike={onDislike}
                    isSavedFilms={isSavedFilms}
                />
        </div>

      {pathname === '/movies' && <button type='button' className={`movies__button ${count >= movies.length && 'movies__button_hidden'}`} onClick={getShowCountMovies}>Ёще</button>}
    </section>
  );
}

export default MoviesCardList;
