//Реакт
import React, { useEffect, useCallback, useState } from "react";

//Компоненты
import Header from "../Header/Header.js";
import SearchForm from "./SearchForm/SearchForm.js";
import MoviesCardList from "./MoviesCardList/MoviesCardList.js";
import Footer from "../Footer/Footer.jsx";

//Стили
import "./Movies.css";

//Апи
import moviesApi from "../../utils/MoviesApi.js";

function Movies({ isLoggedIn, savedMovies, onLike, onDislike }) {
  const [isLoading, setIsLoading] = useState(false);
  const [filteredMovies, setFilteredMovies] = useState([]);
  const [initialCardsMovies, setInitialCardsMovies] = useState([]);
  const [isShortMovies, setIsShortMovies] = useState(false);
  const [isError, setIsError] = useState(false);
  const [isEmptyList, setIsEmptyList] = useState(false);

  function searchMovies(result) {
    localStorage.setItem("movieSearch", result);
    localStorage.setItem("shortMovies", isShortMovies);
    if (localStorage.getItem("allMovies")) {
      const movies = JSON.parse(localStorage.getItem("allMovies"));
      handleFilterMovie(movies, result, isShortMovies);
    } else {
      setIsLoading(true);
      moviesApi
        .getMovies()
        .then((cardsSavedFilms) => {
          handleFilterMovie(cardsSavedFilms, result, isShortMovies);
          setIsError(false);
        })
        .catch((error) => {
          setIsError(true);
          console.log(error);
        })
        .finally(() => {
          setIsLoading(false);
        });
    }
  }

  function counterDurationMovie(movies) {
    return movies.filter((movie) => movie.duration < 40);
  }

  function convertMovieDuration(duration) {
    const minutes = duration % 60;
    const hours = Math.floor(duration / 60);
    return `${hours}ч${minutes}м`;
  }

  function filterMovies(movies, result) {
    const filter = movies.filter((movie) => {
      const movieEn = String(movie.nameEN).toLowerCase().trim();
      const movieRu = String(movie.nameRU).toLowerCase().trim();
      const userSearch = result.toLowerCase().trim();
      return (
        movieRu.indexOf(userSearch) !== -1 || movieEn.indexOf(userSearch) !== -1
      );
    });
    return filter;
  }

  //

  function shortMoviesFilter() {
    setIsShortMovies(!isShortMovies);
    if (!isShortMovies) {
      const filteredCardsMovies = counterDurationMovie(initialCardsMovies);
      setFilteredMovies(filteredCardsMovies);
    } else {
      setFilteredMovies(initialCardsMovies);
    }
    localStorage.setItem("shortMovies", !isShortMovies);
  }

  // Функция фильтрации фильмов
  function handleFilterMovie(movies, query, short) {
    const moviesFilmList = filterMovies(movies, query, short);
    setInitialCardsMovies(moviesFilmList);
    setFilteredMovies(
      short ? counterDurationMovie(moviesFilmList) : moviesFilmList
    );
    localStorage.setItem("movies", JSON.stringify(moviesFilmList));
    localStorage.setItem("allMovies", JSON.stringify(movies));
  }

  // Получение короткометражных фильмов
  useEffect(() => {
    setIsShortMovies(localStorage.getItem("shortMovies") === "true");
  }, []);

  // Получение фильмов из localStorage


  return (
    <>
      <Header isLoggedIn={isLoggedIn} />
      <main className="movies">
        <SearchForm
          isShortMovies={isShortMovies}
          onShortMovies={shortMoviesFilter}
          searchMovies={searchMovies}
        />
        <MoviesCardList
          movies={filteredMovies}
          isLoading={isLoading}
          isSavedFilms={false}
          savedMovies={savedMovies}
          isError={isError}
          onLike={onLike}
          onDislike={onDislike}
          isEmptyList={isEmptyList}
        />
      </main>
      <Footer />
    </>
  );
}

export default Movies;
