import React, { useCallback, useEffect, useState } from "react";
import "./SavedMovies.css";

import Header from "../Header/Header.js";
import SearchForm from "../Movies/SearchForm/SearchForm.js";
import MoviesCardList from "../Movies/MoviesCardList/MoviesCardList.js";
import Footer from "../Footer/Footer.jsx";

function SavedMovies({ isLoggedIn, savedMovies, onDislike }) {
  const [filteredMovies, setFilteredMovies] = useState(savedMovies);
  const [searchedMouvie, setSearchedMovie] = useState("");
  const [isCheck, setIsCheck] = useState(false);
  const [firstSearch, setFirstSearch] = useState(true);

  const filter = useCallback((search, isCheck, movies) => {
    setSearchedMovie(search);
    setFilteredMovies(
      movies.filter((movie) => {
        const searchName = movie.nameRU
          .toLowerCase()
          .includes(search.toLowerCase());
        return isCheck ? searchName && movie.duration <= 40 : searchName;
      })
    );
  }, []);

  function searchMovies(search) {
    setFirstSearch(false);
    filter(search, isCheck, savedMovies);
  }

  useEffect(() => {
    if (savedMovies.length === 0) {
      setFirstSearch(true);
    } else {
      setFirstSearch(false);
    }
    filter(searchedMouvie, isCheck, savedMovies);
  }, [filter, savedMovies, isCheck, searchedMouvie]);

  function changeShort() {
    if (isCheck) {
      setIsCheck(false);
      setFirstSearch(false);
      filter(searchedMouvie, false, savedMovies);
    } else {
      setIsCheck(true);
      setFirstSearch(false);
      filter(searchedMouvie, true, savedMovies);
    }
  }

  return (
    <>
      <Header isLoggedIn={isLoggedIn} />
      <main className="saved-movies">
        <SearchForm
          isCheck={isCheck}
          searchMovies={searchMovies}
          searchedMovie={searchedMouvie}
          changeShort={changeShort}
          setIsError={setIsError}
          setFirstSearch={firstEntrance}
          savedMovie={savedMovies}
        />
        <MoviesCardList
          movies={filteredMovies}
          onDislike={onDislike}
          firstEntrance={firstEntrance}
        />
      </main>
      <Footer />
    </>
  );
}

export default SavedMovies;
