//Реакт
import { Link } from "react-router-dom";
import { useContext, useState, useEffect } from "react";

//Стили
import "./Profile.css";

//Компоненты
import Header from "../../Header/Header.js";
import Footer from "../../Footer/Footer.jsx";

//Контекст и хуки
import useFormWithValidation from "../../../hooks/useForm.js";
import CurrentUserContext from "../../../contexts/CurrentUserContext";

function Profile({ isLoggedIn, onEditInfo, logOut }) {
  const currentUser = useContext(CurrentUserContext);

  const { values, handleChange, errors, isValid, reset } =
    useFormWithValidation();

  function editInfo(e) {
      e.preventDefault();
      onEditInfo({ name: values.name, email: values.email });
  }

  //
  useEffect(() => {
    reset({ name: currentUser.name, email: currentUser.email });
  }, [reset, currentUser]);

  const [isLastValues, setIsLastValues] = useState(false);

  useEffect(() => {
    if (currentUser) { reset(currentUser);
    }
  }, [currentUser, reset]);

  useEffect(() => {
    if (
      currentUser.name === values.name &&
      currentUser.email === values.email
    ) {
      setIsLastValues(true);
    } else {
      setIsLastValues(false);
    }
  }, [values]);
  //

  return (
    <>
      <Header isLoggedIn={isLoggedIn} />
      <main className="profile">
        <h1 className="profile__title">Привет, {currentUser.name}!</h1>

        <form
        isValid={isValid} onSubmit={editInfo}
          values={values}
          className="profile__form"
        >
          <div className="profile__inputs">
            <label className="profile__name">Имя</label>
            <input
              name="name"
              type="text"
              className="profile__input"
              placeholder="Имя"
              minLength={2}
              maxLength={12}
              error={errors.name} //не работает
              value={values.name || ""}
              onChange={handleChange}
              required
            />
          </div>

          <div className="profile__input-line"></div>

          <div className="profile__inputs">
            <label className="profile__name">E-mail</label>
            <input
              name="email"
              type="email"
              className="profile__input"
              placeholder="email@email.ru"
              value={values.email || ""}
              onChange={handleChange}
              required
            />
          </div>
        </form>

        <button
        onSubmit={editInfo}
          type="submit"
          disabled={!isValid ? true : false}
          className={
            !isValid || isLastValues
              ? "profile__button form__button-save_inactive"
              : "profile__button-save"
          }
        >
          Редактировать
        </button>

        <Link to="/" className="profile__link" onClick={logOut}>
          Выйти из аккаунта
        </Link>
      </main>
      <Footer />
    </>
  );
}

export default Profile;
