import { Navigate, Route, Routes, useNavigate } from "react-router-dom";
import { useCallback, useEffect, useState } from "react";
import { apiMain } from "../../utils/MainApi";
import moviesApi from "../../utils/MoviesApi";

import "./App.css";

import ProtectedRoute from "../ProtectedRoute/ProtectedRoute.js";

//Контексты
import CurrentUserContext from "../../contexts/CurrentUserContext.js";
import SendContext from "../../contexts/SendContext.js";
import ErrorContext from "../../contexts/ErrorContext";

import Header from "../Header/Header.js";
import Main from "../Main/Main.js";
import Movies from "../Movies/Movies.js";
import SavedMovies from "../SavedMovies/SavedMovies.js";
import SignUp from "../Users/SignUp/SignUp.js";
import SignIn from "../Users/SignIn/SignIn.js";
import Profile from "../Users/Profile/Profile.js";
import Footer from "../Footer/Footer.jsx";
import NotFoundError from "../NotFoundError/NotFoundError.js";
import Preloader from "../Preloader/Preloader";

function App() {
  const navigate = useNavigate();

  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [currentUser, setCurrentUser] = useState({});

  const [isSend, setIsSend] = useState(false);
  const [savedMovies, setSavedMovies] = useState([]);
  const [isError, setIsError] = useState(false);
  const [isCheckToken, setIsCheckToken] = useState(true);
  const [isSuccess, setIsSuccess] = useState(false);
  const [isEdit, setIsEdit] = useState(false);

  //Добавление данных на страницу
  useEffect(() => {
    if (localStorage.token) {
      Promise.all([
        apiMain.getUserData(localStorage.token),
        apiMain.getMovies(localStorage.token),
      ])
        .then(([userData, dataMovies]) => {
          setSavedMovies(dataMovies.reverse());
          setCurrentUser(userData);
          isLoggedIn(true);
          setIsCheckToken(false);
        })
        .catch((err) => {
          console.error(`Ошибка при загрузке начальных данных ${err}`);
          setIsCheckToken(false);
          localStorage.clear();
        });
    } else {
      setIsLoggedIn(false);
      setIsCheckToken(false);
      localStorage.clear();
    }
  }, [isLoggedIn]);

  const setSuccess = useCallback(() => {
    setIsSuccess(false);
  }, []);

  //Взаимодействие с фильмами
  function handleDeleteMovie(deletemovieId) {
    apiMain
      .deleteMovie(deletemovieId, localStorage.token)
      .then(() => {
        setSavedMovies(
          savedMovies.filter((movie) => {
            return movie._id !== deletemovieId;
          })
        );
      })
      .catch((err) => console.error(`Ошибка при удалении фильма ${err}`));
  }

  function handleToggelMovie(data) {
    const isAdd = savedMovies.some((element) => data.id === element.movieId);
    const seachClickMovie = savedMovies.filter((movie) => {
      return movie.movieId === data.id;
    });
    if (isAdd) {
      handleDeleteMovie(seachClickMovie[0]._id);
    } else {
      apiMain
        .addMovie(data, localStorage.token)
        .then((res) => {
          setSavedMovies([res, ...savedMovies]);
        })
        .catch((err) => console.error(`Ошибка при установке лайка ${err}`));
    }
  }

  //Регистрация и авторизация
  function handleRegister(name, email, password) {
    setIsSend(true);

    return apiMain
      .registration(name, email, password)
      .then(() => {
        handleLogin(email, password);
      })
      .catch((err) => {
        console
          .error(`Ошибка регистрации ${err}`)
          .finally(() => setIsSend(false));
      });
  }

  function handleLogin(email, password) {
    setIsSend(true);

    return apiMain
      .authorization(email, password)
      .then((res) => {
        localStorage.setItem("token", res.token);
        //добавить сообщение про успешный вход
        setIsLoggedIn(true);
        navigate("/movies");
      })
      .catch((err) => {
        console
          .error(`Ошибка авторизации ${err}`)
          .finally(() => setIsSend(false));
      });
  }

  function editUserData(userInfo) {
    setIsSend(true);
    apiMain
      .editUserData(userInfo)
      .then((data) => {
        setCurrentUser(data);
      })
      .catch((err) => {
        console
          .error(`Ошибка редактирования данных ${err}`)
          .finally(() => setIsSend(false));
      });
  }

  function logOut() {
    localStorage.removeItem("token");
    setIsLoggedIn(false);
    navigate("/");
    window.scrollTo(0, 0);
  }

  //Отрисовка страниц
  return (
    <div className="App">
      {isCheckToken ? (
        <Preloader />
      ) : (
        <CurrentUserContext.Provider value={currentUser}>
          <SendContext.Provider value={isSend}>
            <ErrorContext.Provider value={isError}>
              <Routes>
                <Route
                  path="/signin"
                  element={
                    isLoggedIn ? (
                      <Navigate to="/movies" replace />
                    ) : (
                      <SignIn onLogin={handleLogin} setIsError={setIsError} />
                    )
                  }
                />

                <Route
                  path="/signup"
                  element={
                    isLoggedIn ? (
                      <Navigate to="/movies" replace />
                    ) : (
                      <SignUp
                        onRegister={handleRegister}
                        setIsError={setIsError}
                      />
                    )
                  }
                />

                <Route
                  path="/"
                  element={
                    <>
                      <Header isLoggedIn={!isLoggedIn} />
                      <Main />
                      <Footer />
                    </>
                  }
                />

                <Route
                  path="/profile"
                  element={
                    <ProtectedRoute
                      element={Profile}
                      isLoggedIn={isLoggedIn}
                      editUserData={editUserData}
                      logOut={logOut}
                      setIsError={setIsError}
                      isSuccess={isSuccess}
                      setSuccess={setSuccess}
                      setIsEdit={setIsEdit}
                      isEdit={isEdit}
                    />
                  }
                />

                <Route
                  path="/movies"
                  element={
                    <ProtectedRoute
                      element={Movies}
                      isLoggedIn={isLoggedIn}
                      savedMovies={savedMovies}
                      addMovie={handleToggelMovie}
                      setIsError={setIsError}
                    />
                  }
                />

                <Route
                  path="/saved-movies"
                  element={
                    <ProtectedRoute
                      element={SavedMovies}
                      isLoggedIn={isLoggedIn}
                      onDelete={handleDeleteMovie}
                      savedMovies={savedMovies}
                      setIsError={setIsError}
                    />
                  }
                />

                <Route path="*" element={<NotFoundError />} />
              </Routes>
            </ErrorContext.Provider>
          </SendContext.Provider>
        </CurrentUserContext.Provider>
      )}
    </div>
  );
}

export default App;
