//Реакт
import { Navigate, Route, Routes, useNavigate } from "react-router-dom";
import { useCallback, useEffect, useState } from "react";

//Компоненты
import Header from "../Header/Header.js";
import Main from "../Main/Main.js";
import Movies from "../Movies/Movies.js";
import SavedMovies from "../SavedMovies/SavedMovies.js";
import SignUp from "../Users/SignUp/SignUp.js";
import SignIn from "../Users/SignIn/SignIn.js";
import Profile from "../Users/Profile/Profile.js";
import Footer from "../Footer/Footer.jsx";
import NotFoundError from "../NotFoundError/NotFoundError.js";
import Notification from "../Notification/Notification.js";
import Preloader from "../Preloader/Preloader";

//Стили
import "./App.css";

//Api
import { apiMain } from "../../utils/MainApi";

//Контексты
import CurrentUserContext from "../../contexts/CurrentUserContext.js";
import SendContext from "../../contexts/SendContext.js";
import ErrorContext from "../../contexts/ErrorContext";

import ProtectedRoute from "../ProtectedRoute/ProtectedRoute.js";

function App() {
  const navigate = useNavigate();
  const [isLoggedIn, setIsLoggedIn] = useState(false);

  const [currentUser, setCurrentUser] = useState({});
  const [movies, setMovies] = useState([]);

  const [isNotificate, setNotification] = useState(false);
  const [notificationTitle, setNotificationTitle] = useState('');

  const [isSuccess, setIsSuccess] = useState(false);
  const [isCheckToken, setIsCheckToken] = useState(true);

  const [isSend, setIsSend] = useState(false);
  const [isError, setIsError] = useState(false);
  const [isEdit, setIsEdit] = useState(false);


  //Добавление данных на страницу
  useEffect(() => {
    if (localStorage.jwt) {
      Promise.all([
        apiMain.getUser(localStorage.jwt),
        apiMain.getMovies(localStorage.jwt),
      ])
        .then(([currentUser, movies]) => {
          setCurrentUser(currentUser);
          setMovies(movies);
          setIsLoggedIn(true);
          setIsCheckToken(false);
        })
        .catch((err) => {
          console.error(`Ошибка! ${err}`);
          setIsCheckToken(false);
          localStorage.clear();
        });
    } else {
      setIsLoggedIn(false);
      setIsCheckToken(false);
      localStorage.clear();
    }
  }, [isLoggedIn]);

  const setSuccess = useCallback(() => {
    setIsSuccess(false);
  }, []);

  //Взаимодействие с фильмами
  function handleDeleteMovie(deletemovieId) {
    apiMain
      .deleteMovie(deletemovieId, localStorage.jwt)
      .then(() => {
        setSavedMovies(
          savedMovies.filter((movie) => {
            return movie._id !== deletemovieId;
          })
        );
      })
      .catch((err) => console.error(`Не удалось удалить фильм  ${err}`));
  }

  function handleMovieLike(data) {
    const isLike = searchedMovies.some((element) => data.id === element.movieId);
    const seachClickMovie = savedMovies.filter((movie) => {
      return movie.movieId === data.id;
    });
    if (isAdd) {
      handleDeleteMovie(seachClickMovie[0]._id);
    } else {
      apiMain
        .addMovie(data, localStorage.jwt)
        .then((res) => {
          setSavedMovies([res, ...savedMovies]);
        })
        .catch((err) => console.error(`Не удалось сохранить фильм ${err}`));
    }
  }

  //Регистрация, авторизация, редактирование профиля
  function handleRegister({ name, email, password }) {
    return apiMain
      .registration({ name, email, password })
      .then(() => {
        handleLogin({ email, password });
      })
      .catch((err) => {
        setNotification(true);
        setNotificationTitle('Упс! Что-то пошло не так!');
        setIsSuccess(false);
        console.error(`Ошибка регистрации ${err}`)
      })
  }

  function handleLogin({ email, password }) {
    return apiMain
      .authorization({ email, password })
      .then((res) => {
        setIsSuccess(true);
        setNotification(true);
        setNotificationTitle('Отлично! У вас получилось войти!');
        localStorage.setItem("jwt", res.token);
        navigate("/movies");
        setIsLoggedIn(true);
      })
      .catch((err) => {
        setNotification(true);
        setNotificationTitle('Ох! Что-то пошло не так!');
        setIsSuccess(false);
        console.error(`Ошибка авторизации ${err}`)
      })
  }

  function handleEditInfo(name, email) {
    apiMain
      .editUserInfo(name, email, localStorage.jwt)
      .then((data) => {
        setIsSuccess(true);
        setNotification(true);
        setNotificationTitle('Ваши данные успешно обновлены!');
        setCurrentUser(data);
      })
      .catch((err) => {
        setNotification(true);
        setNotificationTitle('Ой-ой! Что-то пошло не так!');
        setIsSuccess(false);
        console.error(`Ошибка редактирования данных ${err}`);
      });
  } 

  function logOut() {
    localStorage.clear();
    setIsLoggedIn(false);
    navigate("/");
    window.scrollTo(0, 0);
  }

  //Открытие и закрытие попапа
  const isOpen = isNotificate;

  function closePopup() {
    setNotification(false);
  }

  useEffect(() => {
    function closeByKeys(e) {
      if (e.key === 'Escape' || e.key === 'Enter') {
        closePopup()
      }
    }
    if (isOpen) {
      document.addEventListener("keydown", closeByKeys)
      return () => {
        document.removeEventListener("keydown", closeByKeys)
      }
    }
  }, [isOpen])

  function closeByOverlay(e) {
    if (e.target === e.currentTarget) {
      closePopup()
    }
  }

  //Отрисовка страниц
  return (
    <div className="App">
      {isCheckToken ? (
        <Preloader />
      ) : (
        <CurrentUserContext.Provider value={currentUser}>
          <SendContext.Provider value={isSend}>
            <ErrorContext.Provider value={isError}>
              <Routes>
                <Route
                  path="/signin"
                  element={
                    isLoggedIn ? (
                      <Navigate to="/movies" replace />
                    ) : (
                      <SignIn onLogin={handleLogin} />
                    )
                  }
                />

                <Route
                  path="/signup"
                  element={
                    isLoggedIn ? (
                      <Navigate to="/movies" replace />
                    ) : (
                      <SignUp
                        onRegister={handleRegister}
                      />
                    )
                  }
                />

                <Route
                  path="/"
                  element={
                    <>
                      <Header isLoggedIn={!isLoggedIn} />
                      <Main />
                      <Footer />
                    </>
                  }
                />

                <Route
                  path="/profile"
                  name='profile'
                  element={
                    <ProtectedRoute
                      element={Profile}
                      isLoggedIn={isLoggedIn}
                      onEditInfo={handleEditInfo}
                      logOut={logOut}
                      isSuccess={isSuccess}
                    />
                  }
                />

                <Route
                  path="/movies"
                  element={
                    <ProtectedRoute
                      element={Movies}
                      isLoggedIn={isLoggedIn}

                      savedMovies={movies}
                      addMovie={handleToggelMovie}
                      setIsError={setIsError}
                    />
                  }
                />

                <Route
                  path="/saved-movies"
                  element={
                    <ProtectedRoute
                      element={SavedMovies}
                      isLoggedIn={isLoggedIn}

                      onDelete={handleDeleteMovie}
                      savedMovies={movies}
                      setIsError={setIsError}
                    />
                  }
                />

                <Route path="*" element={<NotFoundError />} />
              </Routes>
            </ErrorContext.Provider>
          </SendContext.Provider>

          <Notification 
            text={notificationTitle}
            isOpen={isNotificate}
            isSuccess={isSuccess}
            onCloseOverlay={closeByOverlay}
            onClose={closePopup}
          />
        </CurrentUserContext.Provider>
      )}
    </div>
  );
}

export default App;
